import React, { useState } from "react";
import { Link } from "react-router-dom";
// import { HashLink as Link } from 'react-router-hash-link';
import "./css/Header.css";
import {ReactComponent as Heart} from "./heart.svg";

export default function Header() {

    const [active, setActive] = useState(1);

    return (
        <div className="Header-wrapper">
            <ul className="Header-nav">
                <li><Link to="/" onClick={setActive(1)}>home</Link><Heart className={active === 1 ? "heart1" : "heart0"} /></li>
                <li><Link to="/about" onClick={setActive(2)}>about</Link><Heart className={active === 2 ? "heart1" : "heart0"} /></li>
                <li><Link to="/portfolio" onClick={setActive(3)}>portfolio</Link><Heart className={active === 3 ? "heart1" : "heart0"} /></li>
                <li><Link to="/contact" onClick={setActive(4)}>contact</Link><Heart className={active === 4 ? "heart1" : "heart0"} /></li>
                <li><Link to="/blog" onClick={setActive(5)}>blog</Link><Heart className={active === 5 ? "heart1" : "heart0"} /></li>
            </ul>
        </div>
    )
};
