import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import "./css/Header.css";
import {ReactComponent as Heart} from "./heart.svg";
import HeaderNav from './HeaderNav';
import Context from './Context';

export default function Header() {

    <Context.Provider value={{mobile, setMobile}}>
    </Context.Provider>

    const [active, setActive] = useState(1);
    const [mobile, setMobile] = useState(false);

    const toggleMobile = () => {
        !mobile ? setMobile(true) : setMobile(false)
    }

    return (
        <div className="Header-wrapper">

            <div className="Header-nav-mobile">
                <div onClick={toggleMobile} className="cursor">menu</div>
                {mobile && <HeaderNav />}
            </div>

            <ul className="Header-nav">
                <li><Link to="/" onClick={e => setActive(1)} className="Header-item">home</Link><br />
                <Heart className={active === 1 ? "heart1" : "heart0"} /></li>
                <li><Link to="/about" onClick={e => setActive(2)} className="Header-item">about</Link><br />
                <Heart className={active === 2 ? "heart1" : "heart0"} /></li>
                <li><Link to="/portfolio" onClick={e => setActive(3)} className="Header-item">portfolio</Link><br />
                <Heart className={active === 3 ? "heart1" : "heart0"} /></li>
                <li><Link to="/contact" onClick={e => setActive(4)} className="Header-item">contact</Link><br />
                <Heart className={active === 4 ? "heart1" : "heart0"} /></li>
                <li><Link to="/blog" onClick={e => setActive(5)} className="Header-item">blog</Link><br />
                <Heart className={active === 5 ? "heart1" : "heart0"} /></li>
            </ul>
        </div>
    )
};
