import React, { useState } from "react";
import { Link } from "react-router-dom";
// import { HashLink as Link } from 'react-router-hash-link';
import "./css/Header.css";
import {ReactComponent as Heart} from "./heart.svg";
import Dropdown from 'react-bootstrap/Dropdown';

export default function Header() {

    const [active, setActive] = useState(1);

    return (
        <div className="Header-wrapper">
            <Dropdown className="Header-nav-mobile">
                <Dropdown.Toggle  variant="success" id="dropdown-basic" className="center">
                    menu
                </Dropdown.Toggle>

                <Dropdown.Menu className="center">
                    <Dropdown.Item><Link to="/"><span className="Header-item">home</span></Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/about"><span className="Header-item">about</span></Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/portfolio"><span className="Header-item">portfolio</span></Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/contact"><span className="Header-item">contact</span></Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/blog"><span className="Header-item">blog</span></Link></Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>

            <ul className="Header-nav">
                <li><Link to="/" onClick={e => setActive(1)} className="Header-item">home</Link><br />
                <Heart className={active === 1 ? "heart1" : "heart0"} /></li>
                <li><Link to="/about" onClick={e => setActive(2)} className="Header-item">about</Link><br />
                <Heart className={active === 2 ? "heart1" : "heart0"} /></li>
                <li><Link to="/portfolio" onClick={e => setActive(3)} className="Header-item">portfolio</Link><br />
                <Heart className={active === 3 ? "heart1" : "heart0"} /></li>
                <li><Link to="/contact" onClick={e => setActive(4)} className="Header-item">contact</Link><br />
                <Heart className={active === 4 ? "heart1" : "heart0"} /></li>
                <li><Link to="/blog" onClick={e => setActive(5)} className="Header-item">blog</Link><br />
                <Heart className={active === 5 ? "heart1" : "heart0"} /></li>
            </ul>
        </div>
    )
};
