import React, { useState, useEffect } from "react";
import axios from 'axios';
import BlogPost from "./BlogPost";
import "./css/Blog.css";

export default function Blog() {

    const [data, setData] = useState();
    const [keywLinks, setKeywLinks] = useState([]);

    useEffect(() => {
        axios
            .get(`/api/blogposts`)
            .then((response) => setData(response.data.reverse()))
            .catch((error) => {
                console.log(error);
            });
    }, []);

    useEffect(() => {
        const tempKeywords = []
        const filteredKeywords = []

        data && data.forEach((item)=> {
            
            const separate = item.keywords.split(",")
            tempKeywords.push(...separate)
            function filtered(){
                const filterThem = tempKeywords.filter((v, i, a) => a.indexOf(v) === i)
                filteredKeywords.push(filterThem)
                return filterThem
            }

            setKeywLinks(filtered());

            console.log("tempKeywords", tempKeywords)
            console.log("filteredKeywords", filteredKeywords)
        })
    }, [data]);

        console.log("keywLinks", keywLinks)
    
    return (
        <div className="Blog-wrapper">
            Blog
            <div className="Blog-keywords">{keywLinks}</div>
            {data && data.map((item, i)=> {
                return ( 
                    <BlogPost 
                        key={i}
                        title={item.title}
                        date={item.date}
                        postbody={item.postbody}
                        picture={item.picture}
                        keywords={item.keywords}
                    />
                )}
            )}
        </div>
    )
};
