import React, { useState } from "react";
import { Link } from "react-router-dom";
// import { HashLink as Link } from 'react-router-hash-link';
import "./css/Header.css";
import {ReactComponent as Heart} from "./heart.svg";
import Dropdown from 'react-bootstrap/Dropdown';

export default function Header() {

    const [active, setActive] = useState(1);

    return (
        <div className="Header-wrapper">
            <Dropdown className="Header-nav-mobile"  media="(max-width: 650px)">
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                    menu
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item><Link to="/">home</Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/about">about</Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/portfolio">portfolio</Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/contact">contact</Link></Dropdown.Item>
                    <Dropdown.Item><Link to="/blog">blog</Link></Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>

            <ul className="Header-nav"  media="(min-width: 650px)">
                <li><Link to="/" onClick={e => setActive(1)}>home</Link><br />
                <Heart className={active === 1 ? "heart1" : "heart0"} /></li>
                <li><Link to="/about" onClick={e => setActive(2)}>about</Link><br />
                <Heart className={active === 2 ? "heart1" : "heart0"} /></li>
                <li><Link to="/portfolio" onClick={e => setActive(3)}>portfolio</Link><br />
                <Heart className={active === 3 ? "heart1" : "heart0"} /></li>
                <li><Link to="/contact" onClick={e => setActive(4)}>contact</Link><br />
                <Heart className={active === 4 ? "heart1" : "heart0"} /></li>
                <li><Link to="/blog" onClick={e => setActive(5)}>blog</Link><br />
                <Heart className={active === 5 ? "heart1" : "heart0"} /></li>
            </ul>
        </div>
    )
};
